const rows = $input.all();

// 1. Filter: Get a list of ALL rows where social_status is 0
const candidates = rows.filter(row => {
  // Get the value safely
  const status = row.json.social_status;

  // Keep row if status is number 0, string "0", or empty (if you treat empty as ready)
  return status == 0 || status === '0';
});

// 2. Check if we have any candidates left
if (candidates.length > 0) {

  // 3. The Magic: Pick a RANDOM number between 0 and the list length
  const randomIndex = Math.floor(Math.random() * candidates.length);

  // 4. Return the single random row we picked
  return [candidates[randomIndex]];

} else {

  // Return indicator only if all are processed
  return [{
    json: {
      all_inputs_are_1: true,
      message: 'All rows are processed',
    }
  }];
}

